      out.write("	<script type=\"text/javascript\">\r\n");
      out.write("	\r\n");
      out.write("	let form2;\r\n");
      out.write("	\r\n");
      out.write("	\r\n");
      out.write("	//template으로 반복문 처리함\r\n");
      out.write("	const row={\r\n");
      out.write("			template:`\r\n");
      out.write("				<div class=\"row\" style=\"border-bottom: dotted 1px; padding:15px 0 0 0\">\r\n");
      out.write("                <div class=\"col-10\" style=\"padding: 0px 30px 0px 30px;\">\r\n");
