      out.write("                    </p>\r\n");
      out.write("                </div>\r\n");
      out.write("                <div class=\"col-2\" align=\"right\">\r\n");
      out.write("                    <button type=\"button\" class=\"btn btn-dark\" name=\"bt_CCDelete\" value=\"chatterComments.chattercomments_idx\" @click=\"ccDel(chatterComments)\" >삭제</button>\r\n");
      out.write("                </div>\r\n");
      out.write("            </div>\r\n");
      out.write("			`, \r\n");
      out.write("			//4) row 인스턴스 생성 시 외부에서 전달되는 파라미터(json에 담긴 chatterComments)를 받아와 data로 나타냄\r\n");
      out.write("			props:['obj'],\r\n");
      out.write("			data(){\r\n");
      out.write("				return{\r\n");
      out.write("					chatterComments:this.obj\r\n");
      out.write("				};\r\n");
      out.write("			}, \r\n");
      out.write("			methods:{ //인덱스에 해당하는 댓글을 삭제함\r\n");
      out.write("				\r\n");
      out.write("				//댓글 삭제하기\r\n");
      out.write("				ccDel(json){\r\n");
      out.write("					$.ajax({\r\n");
      out.write("						url : \"/rest/community/chatter/\"+ json.chattercomments_idx,\r\n");
      out.write("						type : \"DELETE\",\r\n");
      out.write("						success : function(result, status, xhr) {\r\n");
      out.write("							alert(\"댓글 삭제 완료 :)\");\r\n");
      out.write("							ccList();\r\n");
      out.write("						},\r\n");
      out.write("						error: function(xhr, status, result){\r\n");
      out.write("							console.log(\"xhr : \",xhr);\r\n");
      out.write("							console.log(\"status : \",status);\r\n");
      out.write("							console.log(\"result : \", result);\r\n");
      out.write("						}\r\n");
      out.write("					});\r\n");
      out.write("				}\r\n");
      out.write("			}\r\n");
      out.write("	}\r\n");
      out.write("	\r\n");
      out.write("	form2 = new Vue({\r\n");
      out.write("		el: '#form2',\r\n");
      out.write("		components:{\r\n");
      out.write("			row\r\n");
      out.write("		},\r\n");
      out.write("		data:{\r\n");
      out.write("			chatterCommentsList:[]\r\n");
      out.write("		}\r\n");
      out.write("	});\r\n");
      out.write("	\r\n");
      out.write("		//삭제하기\r\n");
      out.write("		function del() {\r\n");
      out.write("			\r\n");
      out.write("			console.log(\"선택한 글번호 : \", $(\"#form1 input[name='chatter_idx']\").val());\r\n");
      out.write("\r\n");
      out.write("			//삭제 확인하기\r\n");
      out.write("			if (!confirm(\"삭제하시겠습니까?\")) {\r\n");
      out.write("				return; \r\n");
      out.write("			}\r\n");
      out.write("\r\n");
      out.write("			$.ajax({\r\n");
      out.write("				url : \"/community/chatter/del/\"+ $(\"#form1 input[name='chatter_idx']\").val(),\r\n");
      out.write("				type : \"POST\",\r\n");
      out.write("				success : function(result, status, xhr) {\r\n");
      out.write("					alert(\"삭제 완료 :)\");\r\n");
      out.write("					location.href = \"/community/chatterList\" //목록으로 이동\r\n");
      out.write("				},\r\n");
      out.write("				error: function(xhr, status, result){\r\n");
      out.write("					console.log(\"xhr : \",xhr);\r\n");
      out.write("					console.log(\"status : \",status);\r\n");
      out.write("					console.log(\"result : \", result);\r\n");
      out.write("				}\r\n");
      out.write("			});\r\n");
      out.write("		}\r\n");
      out.write("		\r\n");
      out.write("		//댓글 등록하기\r\n");
      out.write("		function ccRegister(){\r\n");
      out.write("\r\n");
      out.write("			//입력값 formData로 담기(전송 대상에 이미지가 포함되기 때문)\r\n");
      out.write("			//누구.누구로 확실하게 표기해줘야 함\r\n");
      out.write("			let formData = new FormData();\r\n");
      out.write("			formData.append(\"chatter.chatter_idx\", $(\"#form1 input[name='chatter_idx']\").val());\r\n");
      out.write("			formData.append(\"mem.mem_idx\", $(\"#form1 input[name='mem_idx']\").val()); //실제 로그인 한 회원으로 수정해야 함------------------------------------\r\n");
      out.write("			formData.append(\"chattercomments_detail\", $(\"textarea[name='chattercomments_detail']\").val()); \r\n");
      out.write("\r\n");
      out.write("			console.log(\"chatter_idx : \", $(\"#form1 input[name='chatter_idx']\").val());\r\n");
      out.write("			console.log(\"mem_idx : \", $(\"#form1 input[name='mem_idx']\").val());\r\n");
      out.write("			console.log(\"chattercomments_detail : \", $(\"textarea[name='chattercomments_detail']\").val());\r\n");
      out.write("			console.log(\"formData : \", formData); \r\n");
      out.write("			\r\n");
      out.write("			//controller로 전송하기\r\n");
      out.write("			$.ajax({\r\n");
      out.write("				url: \"/rest/community/chatter/\"+ $(\"#form1 input[name='chatter_idx']\").val(),\r\n");
      out.write("				type: \"POST\",\r\n");
      out.write("				data : formData, \r\n");
      out.write("				processData : false, //쿼리 스트링화를 방지하기 위함\r\n");
      out.write("				contentType:false, // application/x-www-form~~ 사용안함\r\n");
      out.write("				success : function(result, status, xhr){\r\n");
      out.write("					console.log(\"댓글등록정보\", result.msg);\r\n");
      out.write("					alert(\"댓글 등록 완료:)\");\r\n");
      out.write("					document.getElementById(\"chattercomments_detail\").value=''; //댓글창 비우기\r\n");
      out.write("					ccList();\r\n");
      out.write("				},\r\n");
      out.write("				error: function(xhr, status, result){\r\n");
      out.write("					console.log(\"xhr : \",xhr);\r\n");
      out.write("					console.log(\"status : \",status);\r\n");
      out.write("					console.log(\"result : \", result);\r\n");
      out.write("				}\r\n");
      out.write("			});\r\n");
      out.write("		}\r\n");
      out.write("		\r\n");
