      out.write("	}\r\n");
      out.write("});\r\n");
      out.write("\r\n");
      out.write("function pageLink(n){//원하는 페이지만 넘겨받기\r\n");
      out.write("	//서버에서 가져온 데이터를 대상으로 페이징 로직을 적용해보기\r\n");
      out.write("	pager.init(app1.dcontentsList, n);\r\n");
      out.write("	\r\n");
      out.write("	app1.num=pager.num;\r\n");
      out.write("	//넘겨받은 페이지 번호를 이용하여, 해당 페이지에 보여질 배열을 생성\r\n");
      out.write("	//currentList에 대입 (Vue의 변수인 currentList만 제어하면 디자인은 자동으로 변경)\r\n");
      out.write("	\r\n");
      out.write("	app1.currentList.splice(0, app1.currentList.length);//싹 비우고\r\n");
