      out.write("			pageLink(currentPage); //페이징 처리 계산 수행\r\n");
      out.write("			//프로그램 가동하자마자 현재 페이지는 ? currentPage\r\n");
      out.write("					\r\n");
      out.write("			//넘버링 출력(1~10, 11~20)\r\n");
      out.write("			\r\n");
      out.write("			for(let i=pager.firstPage;i<pager.lastPage;i++){\r\n");
      out.write("				if(i>pager.totalPage) break;//내가 가진 페이지수를 넘어서면 반복문 빠져나오기\r\n");
      out.write("				$(\"#paging-area\").append(\"<a href='javascript:pageLink(\"+i+\")' aria-controls='zero_config' data-dt-idx='1' tabindex='0' class='page-link'>\"+i+\"</a>\")\r\n");
      out.write("\r\n");
      out.write("			}\r\n");
      out.write("		}\r\n");
      out.write("		\r\n");
      out.write("\r\n");
      out.write("		\r\n");
      out.write("	});\r\n");
      out.write("}\r\n");
      out.write("\r\n");
